<?phpinclude "../../../../config/config.php";?><link rel="stylesheet" href="../../function/css/simbada.css" type="text/css" media="screen" /><link href="../../function/tablecloth/tablecloth.css" rel="stylesheet" type="text/css" media="screen" /><script type="text/javascript" src="../../function/tablecloth/tablecloth.js"></script><div align="center" style="padding:5px;background-color:#004933"><span style="font:14px Arial;font-weight:bold;color:whitesmoke;">IMPORT DATA XLS</span></div><br><?php//variabel  $aDoor = $_POST['formDoor'];  $row = $_POST['formDoor'];// nilai awal counter untuk jumlah data yang sukses dan yang gagal diimport$sukses = 0;$gagal = 0;      if(empty($aDoor))  {    echo("<div align='center' style='font:14px Arial;font-weight:bold;'>Tidak ada data yang dipilih</div><br>");  }  else  {    $N = count($aDoor);    echo("<font style='font:14px Arial;font-weight:bold'>Data yang dipilih : </font>");	echo "<table border='1'>			<tr>			<th>No</th>			<th>NAMA BARANG</th>			<th>KODE BARANG</th>			<th>TANGGAL</th>			<th>MERK</th>			<th>BAHAN</th>			<th>SATUAN</th>			<th>KETERANGAN</th>			<th>HARGA</th>			<th>STATUS</th>			</tr>";    for($i=0; $i < $N; $i++)    {		$j=$i+1;	echo "<tr>";		//ambil record yang di cek list		list($nama, $kd_brg, $tanggal, $merk, $bahan, $ket, $harga,$tahun,$satuan) = explode("|", $row[$i]);		//cari kelompok id		$sql_kelompok=mysql_query("SELECT Kelompok_ID FROM Kelompok WHERE Kode='$kd_brg' limit 1");		$hasil_kelompok=mysql_fetch_object($sql_kelompok);		$kd_brg=$hasil_kelompok->Kelompok_ID;						  // setelah data dibaca, sisipkan ke dalam tabel mhs		$query = "INSERT INTO StandarHarga (StandarHarga_ID,NamaAset,Kelompok_ID,Merk,TglUpdate,Spesifikasi,Keterangan,                    NilaiStandar,Tahun,Satuan) VALUES ('', '$nama','$kd_brg', '$merk', '$tanggal', '$bahan', '$ket' ,'$harga','$tahun','$satuan')";		$hasil = mysql_query($query);				// jika proses insert data sukses, maka counter $sukses bertambah	// jika gagal, maka counter $gagal yang bertambah	if ($hasil) {	$sukses++;	$status="Berhasil di Upload";	}	else {	$gagal++;	$status="Gagal di Upload";	}			//menampilkan status data yang dipilih		  echo "<td width='50' style='text-align:center'>$j</td><td>$nama</td><td>$kd_brg</td><td>$tanggal</td>		<td>$merk</td><td>$bahan</td><td>$satuan</td><td>$ket</td><td>$harga</td><td>$status</td></tr>";    }  }  echo "</table>";    // tampilan status sukses dan gagalecho "<div align='center'><hr width='500px'><br><font style='font:14px Arial;font-weight:bold;'>Proses import data selesai</font>";echo "<p>Jumlah data yang sukses diimport : ".$sukses."<br>";echo "Jumlah data yang gagal diimport : ".$gagal."</p>";?><br><a href="javascript:window.back()" style="font:14px Arial;color:#0066FF;text-decoration:underline;">&nbsp&nbspImport Data Lagi&nbsp&nbsp</a>